  cb(null, tablesJSON);
}

function isComment(line) {
  return line
    .trim()
    .startsWith('--');
}

function isValid(line) {
  if (line !== "") {
    for (var i = 0; i < data.ignore.length; i++) {
      if (line.startsWith(data.ignore[i])) {
        return false;
      }
    }
  } else {
    return false;
  }
  return true;
}

